@import url("https://fonts.googleapis.com/css?family=Montserrat:400,500,600");
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0; }

html {
  font-size: 10px; }

body {
  background-color: #232323;
  color: #e0e0e0;
  font-family: 'Montserrat', sans-serif; }

#root {
  display: flex;
  justify-content: center; }

h1 {
  width: 40%;
  height: 100vh;
  padding: 1.5rem;
  text-align: center;
  background-color: #473978;
  display: flex;
  justify-content: center;
  flex-direction: column; }

h1 span {
  font-size: 1.4rem;
  display: block; }

h1 span.title {
  font-size: 5rem; }

/* game */
.game {
  padding: 1.5rem;
  width: 60%;
  height: 100vh;
  display: flex;
  justify-content: center;
  flex-direction: column; }

.score {
  text-align: center;
  font-size: 1.4rem;
  font-weight: 500;
  margin-bottom: 3rem; }

h3 {
  margin-bottom: 3rem;
  font-size: 3rem;
  text-align: center; }

.answers {
  width: 100%;
  text-align: center; }

.answers li {
  list-style: none;
  display: block;
  width: 50px;
  height: 50px;
  background-color: #232323;
  display: inline-block;
  border-radius: 50px;
  margin-right: 20px;
  box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.3), 0px 1px 2px 1px rgba(0, 0, 0, 0.1), 0px 3px 4px 4px rgba(0, 0, 0, 0.06);
  -webkit-transition: all 0.45s ease;
  -ms-transition: all 0.45s ease;
  -o-transition: all 0.45s ease;
  -transition: all 0.45s ease;
  transition: all 0.45s ease;
  cursor: pointer; }

.answers li.show {
  -webkit-transform: scale(46);
  -ms-transform: scale(46);
  -o-transform: scale(46);
  -transform: scale(46);
  transform: scale(46);
  -webkit-transition: -webkit-transform 0.65s ease;
  -ms-transition: -ms-transform 0.65s ease;
  -o-transition: -o-transform 0.65s ease;
  -transition: -transform 0.65s ease;
  transition: transform 0.65s ease; }

.overlay {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 9; }

.overlay .button {
  display: inline-block;
  margin: 30px 10px 0px;
  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.1);
  border: 2px solid white;
  border-radius: 5px;
  padding: 7px;
  font-size: 13pt;
  background-color: transparent;
  cursor: pointer; }

.overlay .correct, .overlay .wrong {
  position: relative;
  top: 50%;
  -webkit-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  transform: translateY(-50%);
  text-shadow: 0px 1px 3px rgba(0, 0, 0, 0.1);
  margin: 0 auto;
  display: none;
  text-align: center;
  font-size: 300%;
  color: white;
  text-transform: uppercase;
  font-weight: 800;
  line-height: 1.2;
  -webkit-transition: all 500ms cubic-bezier(0.16, 0.935, 0.31, 1);
  -webkit-transition: all 500ms cubic-bezier(0.16, 0.935, 0.31, 1.2);
  -moz-transition: all 500ms cubic-bezier(0.16, 0.935, 0.31, 1.2);
  -o-transition: all 500ms cubic-bezier(0.16, 0.935, 0.31, 1.2);
  transition: all 500ms cubic-bezier(0.16, 0.935, 0.31, 1.2);
  -webkit-transition-timing-function: cubic-bezier(0.16, 0.935, 0.31, 1);
  -webkit-transition-timing-function: cubic-bezier(0.16, 0.935, 0.31, 1.2);
  -moz-transition-timing-function: cubic-bezier(0.16, 0.935, 0.31, 1.2);
  -o-transition-timing-function: cubic-bezier(0.16, 0.935, 0.31, 1.2);
  transition-timing-function: cubic-bezier(0.16, 0.935, 0.31, 1.2); }

